<script>
import AsideMenuListSkeleton from '@/components/AsideMenu/AsideMenuListSkeleton.vue'
import AsideMenuListItem from '@/components/AsideMenu/AsideMenuListItem.vue'

export default {
  components: {
    AsideMenuListSkeleton,
    AsideMenuListItem
  },
  props: {
    menu: {
      type: Array,
      default: () => []
    }
  },
  computed: {
    status () {
      return this.$store.state.navigator.status
    },
    isAccountSelected () {
      return this.$store.state.navigator.currentSettingsTab === 'account'
    },
    isTarifSelected () {
      return this.$store.state.navigator.currentSettingsTab === 'tarif'
    },
    isOptionSelected () {
      return this.$store.state.navigator.currentSettingsTab === 'option'
    },
    isKarmaSelected () {
      return this.$store.state.navigator.currentSettingsTab === 'karma'
    },
    isSupportSelected () {
      return this.$store.state.navigator.currentSettingsTab === 'support'
    }
  },
  methods: {
    gotoSettingsTab (type, label, uid) {
      this.$store.state.navigator.currentSettingsTab = type
      const navElem = {
        name: label,
        key: 'greedSource',
        value: { uid: uid, param: null },
        greedPath: type
      }
      localStorage.setItem('currentSettingsTab', type)
      localStorage.setItem('lastTab', 'settings')
      this.$store.commit('updateStackWithInitValue', navElem)
      // закрываем сабменю и меняем роут
      this.$store.state.navigator.submenu.status = false
      this.$router.push('/settings')
    },
    gotoAccount () {
      this.gotoSettingsTab('account', 'Аккаунт', '9d3ba501-c173-462d-9b5f-0db97c06a026')
    },
    gotoTarif () {
      this.gotoSettingsTab('tarif', 'Тариф', '0853812c-cf1c-4037-b2b6-b7020cb68105')
    },
    gotoOption () {
      this.gotoSettingsTab('option', 'Настройки', '88a45ac3-ac5d-486b-88bb-b96874580338')
    },
    gotoKarma () {
      this.gotoSettingsTab('karma', 'Карма', '5cda53e7-f859-4bac-b76c-4ad330a65a9a')
    },
    gotoSupport () {
      this.gotoSettingsTab('support', 'Помощь', '6eafh4l1-x4cn-7gbn-m9fb-6jk2ds073p1o')
    }
  }
}
</script>
